let numero1=document.getElementById("numero1"),numero2=document.getElementById("numero2"),botao=document.getElementById("calcular"),res=document.getElementById("resultado");botao.addEventListener("click",(function(){let n1=parseFloat(numero1.value),n2=parseFloat(numero2.value);isNaN(n1)||isNaN(n2)?res.innerHTML="Por favor, insira números válidos!":res.innerHTML="Resultado: "+calcular(n1,n2)}));